mapper.select:
select

from tb_host
where container = 1 AND parent_host_name = #{hostName,jdbcType=VARCHAR}

SELECT DISTINCT t1.id AS host_id, t1.host_name as host_name, t1.ip as host_ip, t1.container as host_type, t5.agent_health_level as agent_health_level, t5.agent_health_description as agent_health_description, t5.agent_health_inspection_result_type as agent_health_inspection_result_type, t3.agent_version_id as agent_version_id, t1.machine_zone as host_machine_zone, t1.create_time as host_create_time, t3.id as agent_id, t1.parent_host_name as parent_host_name FROM tb_host as t1 LEFT JOIN tb_service_host as t2 ON t2.host_id = t1.id LEFT JOIN tb_agent as t3 ON t3.host_name = t1.host_name LEFT JOIN tb_agent_health as t5 ON t5.agent_id = t3.id
1 = 1
and t1.host_name like concat('%',#{hostName},'%')
and t1.ip like concat('%',#{ip},'%')
and t3.agent_version_id in
#{agentVersionId}
and t5.agent_health_level in
#{agentHealthLevel}
and t1.container in
#{container}
and t2.service_id in
#{serviceId}
and t1.machine_zone in
#{machineZone}
and t1.create_time >= #{createTimeStart}
and #{createTimeEnd} >= t1.create_time
order by
${sortColumn}
asc
desc
host_create_time desc
limit #{limitFrom}, #{limitSize}

select count(*) from (
SELECT
DISTINCT t1.id AS host_id
FROM tb_host as t1
LEFT JOIN tb_service_host as t2 ON t2.host_id = t1.id
LEFT JOIN tb_agent as t3 ON t3.host_name = t1.host_name
LEFT JOIN tb_agent_health as t5 ON t5.agent_id = t3.id

  1 = 1

    and t1.host_name like concat('%',#{hostName},'%')


    and t1.ip like concat('%',#{ip},'%')


    and t3.agent_version_id in

      #{agentVersionId}



    and t5.agent_health_level in

      #{agentHealthLevel}



    and t1.container in

      #{container}



    and t2.service_id in

      #{serviceId}



    and t1.machine_zone in

      #{machineZone}



    and t1.create_time >= #{createTimeStart}


    and #{createTimeEnd} >= t1.create_time


) as t4
